import React, { useEffect, useState } from "react";
import { fetchData } from "../helpers/fetchHelper";
import { json } from "react-router-dom";

export default function CategoryList({ handleCategoryClick, currentCategory }) {
  const [categories, setCategories] = useState([]);
  const [fetchError, setFetchError] = useState();
  useEffect(() => {
    fetchData("https://fakestoreapi.com/products/categoriess")
      .then((result) => {
        setCategories(result);
      })
      .catch((err) => setFetchError(err));
  }, []);

  const categoryList = categories.map((category) => {
    const categoryName = `FAKE: ${category}`;
    return (
      <div
        key={categories.indexOf(category)}
        onClick={handleCategoryClick}
        title={categoryName}
        className={`category--item ${
          categoryName === currentCategory ? "active" : ""
        }`}
      >
        {categoryName}
      </div>
    );
  });
  return fetchError ? (
    <p>Something went wrong. Error Message:{JSON.stringify(fetchError)}</p>
  ) : (
    <div className="categories">{categoryList}</div>
  );
}
