import React, { useState } from "react";
import Header from "../components/Header";
import CategoryList from "../components/CategoryList";
import ProductList from "../components/ProductList";
import {
  FavouritesContext,
  useFavouritesContext,
} from "../context/FavouritesContext";

export default function HomePage({ errorHandler }) {
  const [favContext, handleFavContext] = useFavouritesContext();
  const [currentCategory, setCurrentCategory] = useState("all");

  const handleCategoryClick = (event) => {
    if (!(currentCategory === event.target.title)) {
      setCurrentCategory(event.target.title);
    }
  };
  return (
    <>
      <Header />
      <CategoryList
        errorHandler={errorHandler}
        handleCategoryClick={handleCategoryClick}
        currentCategory={currentCategory}
      />
      <FavouritesContext.provider value={favContext}>
        <ProductList
          errorHandler={errorHandler}
          currentCategory={currentCategory}
          handleFavContext={handleFavContext}
        />
      </FavouritesContext.provider>
    </>
  );
}
