import React, { useEffect, useState } from "react";
import allProducts from "../fake-data/all-products";
import { fetchData } from "../helpers/fetchHelper";

export default function ProductList({ currentCategory }) {
  const [products, setProducts] = useState();
  useEffect(() => {
    if (currentCategory === "all") {
      fetchData("https://fakestoreapi.com/products").then((result) =>
        setProducts(result)
      );
    } else {
      fetchData(
        `https://fakestoreapi.com/products/category/${currentCategory}`
      ).then((result) => (products = result));
    }
  }, []);
  const productList = products.map((product) => {
    return (
      <li key={products.indexOf(product)} className="products--item">
        <div className="product">
          <img
            className="product--image"
            src={product.image}
            alt={product.title}
          />
          <span className="product--title">{product.title}</span>
        </div>
      </li>
    );
  });

  return <ul className="products">{productList}</ul>;
}
