import React, { useEffect, useState } from "react";
import fetchData from "../helpers/fetchHelper";
import useFetch from "../helpers/useFetch";
import heartSolid from "../assets/heart-solid.svg";
import heartRegular from "../assets/heart-regular.svg";

export default function ProductList({ currentCategory, errorHandler }) {
  let url;
  if (currentCategory === "all") {
    url = "https://fakestoreapi.com/products";
  } else {
    url = `https://fakestoreapi.com/products/category/${currentCategory.replace(
      "FAKE: ",
      ""
    )}`;
  }
  const [products, loading] = useFetch(url, errorHandler);
  // const [products, setProducts] = useState([]);
  // const [loading, setLoading] = useState(false);
  // useEffect(() => {
  //   if (currentCategory === "all") {
  //     setLoading(true);
  //     fetchData("https://fakestoreapi.com/products")
  //       .then((result) => setProducts(result))
  //       .catch((err) => errorHandler(err))
  //       .finally(() => setLoading(false));
  //   } else {
  //     setLoading(true);
  //     fetchData(
  //       `https://fakestoreapi.com/products/category/${currentCategory.replace(
  //         "FAKE: ",
  //         ""
  //       )}`
  //     )
  //       .then((result) => {
  //         setProducts(result);
  //       })
  //       .catch((err) => errorHandler(err))
  //       .finally(() => setLoading(false));
  //   }
  // }, [currentCategory]);

  const productList = products
    ? products.map((product) => {
        return (
          <li key={products.indexOf(product)} className="products--item">
            <a href={`/product/${product.id}`}>
              <div className="product">
                <div className="product-image-container">
                  <div
                    onClick={(e) => {
                      e.preventDefault();
                      console.log("click");
                    }}
                    className="product-favourite-container"
                  >
                    <img
                      className="product-image--favourite"
                      src={heartRegular}
                    />
                  </div>
                  <img
                    className="product--image"
                    src={product.image}
                    alt={product.title}
                  />
                </div>

                <span className="product--title">{product.title}</span>
              </div>
            </a>
          </li>
        );
      })
    : [];

  return loading ? <p>loading</p> : <ul className="products">{productList}</ul>;
}
